import React from 'react'
import TableRow from './TableRow'
import moment from 'moment';

class Compare extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            apikey: this.props.apikey,
            date: this.props.date,
            result: []
        };
    }
    componentWillReceiveProps(nextProps) {
        this.setState({ date: nextProps.data });  
    }
    // componentDidUpdate() {
    //     this.componentDidMount();
    // }
    componentDidMount() {
        console.log("refreshed");
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");

        var raw = JSON.stringify({"apikey":this.props.apikey,"method":"partner_api.get_brands","params":{"projectname":"Automotive"}});

        var requestOptions = {
            method: 'POST',
            headers: myHeaders,
            body: raw,
            redirect: 'follow'
        };

        fetch("http://localhost:3001/requests", requestOptions)
            .then(response => response.json())
            .then(json => json.result)
            .then(res => this.setState({ 'result': res }));
        
        var unixTimestamp = moment('2012.08.10', 'YYYY.MM.DD').unix();
    }



    render() {
        return (
            <div>
                <table className="table table-hover">
                    <thead>
                        <tr>
                        <th scope="col">#</th>
                        <th scope="col">Brand Name</th>
                        <th scope="col">Total Profiles</th>
                        <th scope="col">Total Followers</th>
                        <th scope="col">Total Engagement</th>
                        </tr>
                    </thead>
                    <tbody>
                        {(this.state.result.length > 0) ?
                            this.state.result.map((value, index) => {
                                return <TableRow
                                        index={index + 1}
                                        name={value.brandname}
                                        profiles={value.profiles.length}/>
                            })
                            : null
                        }
                        
                    </tbody>
                </table>
            </div>
        )
    }
}

export default Compare;

