import React from "react"
import Compare from "./Compare"
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";

class DatePicker extends React.Component {
    constructor(props) {
        super(props);
        this.state = {apikey: this.props.apikey, date: ''};

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        this.setState({date: event.target.value});
    }

    render() {
        return(
            <Router><div className="container">
                <div className="row pt-5">
                    <div className="col-3"></div>
                    <div className="col-6">
                        <form>
                            <label for="date">Pick a date:</label>
                            <input type="date" id="date1"></input>
                            <Link to="/compare"><input
                                type="submit"
                                value={this.state.date}
                                onSubmit={this.handleChange}></input></Link>
                        </form>
                    </div>
                    <div className="col-3"></div>
                </div>
            </div>
            <Switch>
                <Route path="/compare">
                    <Compare apikey={this.props.apikey} date={this.state.date} />
                </Route>
            </Switch>
            </Router>
        )
    }
}

export default DatePicker;
