import React from "react"

class Form extends React.Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        alert('Apikey was submitted: ' + this.state.value);
        event.preventDefault();
    }
    render() {
        return(
            <div class="container">
                <div class="row pt-5">
                    <div class="col-3"></div>
                    <div class="col-6">
                        <form>
                            <div class="form-group">
                                <label htmlfor="inputApiKey">Enter api-key</label>
                                <input type="apikey" class="form-control" id="inputApiKey1" aria-describedby="apiKey" placeholder="Enter api-key"></input>
                            </div>
                            <button
                                type="submit"
                                class="btn btn-primary"
                                value={this.state.value}
                                onSubmit={this.handleChange}>Submit</button>
                        </form>
                    </div>
                    <div class="col-3"></div>
                </div>
            </div>
        )
    }
}

export default Form;
