import React from "react"

class Card extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            display: false
        }
        this.switchDisplayPhN = this.switchDisplayPhN.bind(this);
    }
    switchDisplayPhN = function(event) {
        this.setState({
            display: true
        });
    }
    render() {
        return(
            <div className="card col">
                <img className="card-image-top img-fluid" src={this.props.person.image} />
                <div className="card-body">
                    <p>{this.props.person.name}</p>
                    <button className="btn-dark" onClick={this.switchDisplayPhN}>
                        {this.state.display === false ? "Show Number" : this.props.person.phone}
                    </button>
                </div>
            </div>
        )
    }
}

export default Card;
