import React from 'react'
import TableRow from './TableRow'

class Compare extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            apikey: this.props.apikey,
            date: this.props.date,
            result: []
        };
    }
    componentDidMount() {
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");

        var raw = JSON.stringify({"apikey":this.props.apikey,"method":"partner_api.get_brands","params":{"projectname":"Automotive"}});

        var requestOptions = {
            method: 'POST',
            headers: myHeaders,
            body: raw,
            redirect: 'follow'
        };

        fetch("http://localhost:3001/requests", requestOptions)
            .then(response => response.json())
            .then(json => json.result)
            .then(res => this.setState({ 'result': res }));
    }

    render() {
        const items = [];
        if (this.state.result.length > 0)
            for (const [index, value] of this.state.result) {
                items.push(
                    <TableRow index={index.stringify}
                            name={this.state.result[index].brandname}
                            profiles={this.state.result[index].profiles.length}/>
                )
            }
        return (
            <div>
                <table className="table table-hover">
                    <thead>
                        <tr>
                        <th scope="col">#</th>
                        <th scope="col">Brand Name</th>
                        <th scope="col">Total Profiles</th>
                        <th scope="col">Total Followers</th>
                        <th scope="col">Total Engagement</th>
                        </tr>
                    </thead>
                    <tbody>
                        {(this.state.result.length > 0) ?
                            {items}
                            // <TableRow index="1" name={this.state.result[0].brandname} profiles={this.state.result[0].profiles.length}/> :
                            : null
                        }
                        
                    </tbody>
                </table>
            </div>
        )
    }
}

export default Compare;

