import React from "react"

class Card extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            display: false
        }
    }
    render() {
        return(
            <div className="card col">
                <img className="card-image-top img-fluid" src={this.props.person.image} />
                <div className="card-body">
                    <p>{this.props.person.name}</p>
                    <button className="btn-dark">Show Number</button>
                    <p>{this.props.person.phone}</p>
                </div>
            </div>
        )
    }
}

export default Card;
