import React from 'react'
// import axios from 'axios'

class Compare extends React.Component {
    constructor(props) {
        super(props);
        this.state = {apikey: this.props.apikey, date: this.props.date, data: ''};
        this.getBrands = this.getBrands.bind(this);
    }
    getBrands() {
        console.log("something is happening");
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");

        var raw = JSON.stringify({"apikey":this.state.apikey,"method":"partner_api.get_brands","params":{"projectname":"Automotive"}});

        var requestOptions = {
        method: 'POST',
        headers: myHeaders,
        body: raw,
        redirect: 'follow'
        };

        fetch("http://localhost:3001/requests", requestOptions)
        .then(res => res.json())
        .then((result) => {
            this.setState({data: result.text()})
        });
    }
    render() {
        return (
            <div>
                <button className="btn btn-primary mt-5"
                        type="submit"
                        onChange={console.log("changed"), this.getBrands}
                        onSubmit={console.log("submitted"), this.getBrands}>Get data</button>
                <h3>Result: {this.data}</h3>
            </div>
        )
    }
}

export default Compare;

